import React from 'react';
import { Link } from 'react-scroll';
import './App.css';

function App() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Calculate background color based on scroll position
  const mainBackground = {
    backgroundColor: `rgba(${255 - scrollY / 5}, ${255 - scrollY / 5}, ${255 - scrollY / 5}, 1)`,
    color: 'white',
    textAlign: 'center',
    padding: '20px',
  };
  return (
    <div className="App">
      <header className="App-header">
        <div className="typing-text">Itai Raviv - DevOps Engineer</div>
        <nav>
          {/* Changed to buttons */}
          <Link activeClass="active" to="abouts" spy={true} smooth={true} offset={-70} duration={500}>
            <button>Abouts</button>
          </Link>
          <Link activeClass="active" to="resume" spy={true} smooth={true} offset={-70} duration={500}>
            <button>Resume</button>
          </Link>
          <Link activeClass="active" to="links" spy={true} smooth={true} offset={-70} duration={500}>
            <button>Links</button>
          </Link>
        </nav>
      </header>
      <main>
        <section id="abouts">
          <h2>Abouts</h2>
          <p>This section contains information about me.</p>
        </section>
        <section id="resume">
          <h2>Resume</h2>
          <p>This section showcases my resume.</p>
        </section>
        <section id="links">
          <h2>Links</h2>
          <p>Here are some important links.</p>
        </section>
      </main>
    </div>
  );
}

export default App;
